    def calcTotal(self):
        prices = {
            "tayTrang": 100000,
            "niengRang": 200000,
            "nhoRang": 50000,
            "trongRang": 500000,
        }
        sum = (0 if self.tay.checkState() == 0 else prices["tayTrang"]) \
            + (0 if self.nieng.checkState() == 0 else prices["niengRang"]) \
            + int(self.nho.text()) * prices["nhoRang"] \
            + int(self.trong.text()) * prices["trongRang"]
        self.sotien.setText(str(sum))

        msg = QtWidgets.QMessageBox()
        msg.setIcon(QtWidgets.QMessageBox.Information)
        msg.setText("Chào {} {}!".format("Ông" if self.gioi.currentText() == "Nam" else "Bà", self.nhapten.text()))
        msg.setInformativeText(" Tổng chi phí là {}vnđ".format(str(sum)))
        msg.setWindowTitle("Chi phí")
        msg.exec_()


